mapper.select:
SELECT id, title, description, status, created_at, updated_at, completed_at
FROM ticket
WHERE id = #{id}

SELECT DISTINCT t.id, t.title, t.description, t.status, t.created_at, t.updated_at, t.completed_at FROM ticket t
INNER JOIN ticket_tag tt ON t.id = tt.ticket_id WHERE tt.tag_id = #{tagId}
AND t.title LIKE CONCAT('%', #{keyword}, '%')
WHERE t.title LIKE CONCAT('%', #{keyword}, '%')
ORDER BY t.created_at DESC LIMIT #{offset}, #{limit}
